    async def parse_order(self, order, market=None):
        # {
        #   'orderId':'E161183624377614',
        #   'memberId':2,
        #   'type':'LIMIT_PRICE',
        #   'amount':1000.0,
        #   'symbol':'BCH/USDT',
        #   'tradedAmount':1000.0,
        #   'turnover':1080.0,
        #   'coinSymbol':'BCH',
        #   'baseSymbol':'USDT',
        #   'status':'COMPLETED',
        #   'direction':'SELL',
        #   'price':1.0,
        #   'time':1611836243776,
        #   'completedTime':1611836256242,
        # },
        type = 'market'
        if order['type'] == 'LIMIT_PRICE':
            type = 'limit'
        side = order['direction'].lower()
        remaining = order['amount'] - order['tradedAmount']
        status = order['status']
        if status == 'COMPLETED':
            status = 'closed'
        elif status == 'TRADING':
            status = 'open'
        else:
            status = 'canceled'
        cost = order['tradedAmount'] * order['price']
        result = {
            'info': order,
            'id': order['orderId'],
            'clientOrderId': order['memberId'],
            'timestamp': order['time'],
            'datetime': self.iso8601(order['time']),
            'lastTradeTimestamp': None,
            'symbol': order['symbol'],
            'type': type,
            'timeInForce': None,
            'postOnly': None,
            'side': side,
            'price': order['price'],
            'stopPrice': None,
            'cost': cost,
            'average': None,
            'amount': order['amount'],
            'filled': order['tradedAmount'],
            'remaining': remaining,
            'status': status,
            'fee': None,
            'trades': None,
        }
        return result

    async def create_order(self, symbol, type, side, amount, price=None, params={}):
        params['symbol'] = symbol
        params['price'] = price
        params['amount'] = amount
        if side == 'buy':
            params['direction'] = 'BUY'
        else:
            params['direction'] = 'SELL'
        if type == 'market':
            params['type'] = 'MARKET_PRICE'
        else:
            params['type'] = 'LIMIT_PRICE'
        params['useDiscount'] = '0'
        response = await self.privatePostExchangeOrderAdd(params)
        orderId = self.safe_string(response, 'data')
        return await self.fetch_order(orderId)

